def main():
    vocab_path = Path(__file__).parent / 'data' / 'exercises' / 'vocabulary.json'
    
    with open(vocab_path, 'r') as f:
        data = json.load(f)
    
    updated = 0
    warnings = 0
    
    for exercise in data['exercises']:
        new_desc = get_coach_description(exercise['name'], exercise['category'])
        if new_desc != "‚ö†Ô∏è":
            exercise['description'] = new_desc
            updated += 1
            print(f"‚úÖ Updated: {exercise['name']}")
        else:
            exercise['description'] = "‚ö†Ô∏è"
            warnings += 1
            print(f"‚ö†Ô∏è  Needs research: {exercise['name']}")
    
    # Save updated data
    with open(vocab_path, 'w') as f:
        json.dump(data, f, indent=4)
    
    print(f"\nüéâ Done! Updated {updated} exercises, {warnings} need research.")

if __name__ == '__main__':
    main()